/* PROCEDURE addContent */

DROP PROCEDURE IF EXISTS addContent;

DELIMITER $$
CREATE PROCEDURE addContent(IN userIN INT, IN titleIN VARCHAR(45), IN albumIN VARCHAR(45), IN artistIN VARCHAR(45), IN genreIN VARCHAR(45), IN contentIN TEXT)
BEGIN
    DECLARE songExists TINYINT DEFAULT 0;
    DECLARE songIN INT;
    DECLARE versionIN INT; -- versionIN è un "contatore" di versioni per ogni canzone caricata da UN utente
    
    SET songExists = (
                                         SELECT COUNT(*)
                                         FROM song s
                                         WHERE s.title = titleIN
                                             AND s.album = albumIN 
                                             AND s.artist = artistIN
                                             AND s.genre = genreIN
                                        );
 

    IF songExists = 0 THEN
        INSERT INTO song (title, album, artist, genre)
        VALUES (titleIN, albumIN, artistIN, genreIN);
    END IF;
    
    SET songIN = (  
                                    SELECT s.id
                                    FROM song s
                                    WHERE s.title = titleIN
                                            AND s.album = albumIN 
                                            AND s.artist = artistIN
                                            AND s.genre = genreIN
                                    LIMIT 1
                                );
                            
    SET versionIN = (
                                        SELECT IFNULL(MAX(c.version), 0) + 1
                                        FROM content c
                                        WHERE c.user = userIN AND c.song = songIN
                                      );
                                      
    INSERT INTO content (song, user, version, uploadDate, avgRating, content)
    VALUES (songIN, userIN, versionIN, CURRENT_DATE, NULL, contentIN);
    
END$$
DELIMITER ;

/* PROCEDURE deleteContent */

DROP PROCEDURE IF EXISTS deleteContent;

DELIMITER $$
CREATE PROCEDURE deleteContent(IN idContentIN INT)
BEGIN
    DECLARE songID INT ;
    DECLARE numContent INT;
  
    SET songID = (
                                  SELECT s.id
                                  FROM song s INNER JOIN content c ON s.id = c.song
                                  WHERE c.id = idContentIN
                                 );
 
    SET numContent = (
                                            SELECT COUNT(*)
                                            FROM content
                                            WHERE song = songID
                                          );
                                          
    -- cancella il contenuto
        DELETE FROM content
        WHERE id = idContentIN;
    -- cancella eventuali valutazioni al contenuto
        DELETE FROM rating
        WHERE content = idContentIN;
    
    -- elimina la row da song solo se idContentIN è l'ultimo contenuto a cui è collegato la row
    IF numContent = 1 THEN
        DELETE FROM song
        WHERE id = songID;
    END IF;
 
END$$
DELIMITER ;

/* Procedure editContent */

DROP PROCEDURE IF EXISTS editContent;

DELIMITER $$
CREATE PROCEDURE editContent(IN idContentIN INT, IN titleIN VARCHAR(45), IN albumIN VARCHAR(45), IN artistIN VARCHAR(45), IN genreIN VARCHAR(45), IN contentIN TEXT)
BEGIN
    DECLARE infoNotModified TINYINT DEFAULT 1;
    DECLARE idNewSong INT; -- id NUOVA song nel caso che infoNotModified = 0
    DECLARE idSong INT; 
    DECLARE numContent INT; -- numero di contenuti correlati alla song di idContentIN
    
    SET idSong = (
                                    SELECT c.song
                                    FROM content c
                                    WHERE c.id = idContentIN
                                );
    
    SET infoNotModified = ( -- Se le informazioni della song non sono state modificate, COUNT <> 0 altrimenti non esiste nessuna song con tali info
                                                    SELECT COUNT(*)
                                                    FROM song s
                                                    WHERE s.title = titleIN 
                                                        AND s.album = albumIN 
                                                        AND s.artist = artistIN 
                                                        AND s.genre = genreIN 
                                                  );
                                                  
    SET numContent = (
                                            SELECT COUNT(*)
                                            FROM content c 
                                            WHERE c.song = idSong
                                          );

    IF infoNotModified = 0 THEN

        
        INSERT INTO song (title, album, artist, genre)
        VALUES(titleIN, albumIN, artistIN, genreIN);
        
        -- recupero id della song appena inserita ...
        SET idNewSong = (
                                      SELECT s.id
                                      FROM song s
                                      WHERE s.title = titleIN 
                                        AND s.album = albumIN 
                                        AND s.artist = artistIN 
                                        AND s.genre = genreIN
                                    );
        
        -- ... e aggiorno l'id della song in content con quello nuovo
        UPDATE content c
        SET c.song = idNewSong
        WHERE c.id = idContentIN;
        
        IF numContent = 1 THEN -- se è rimasto l'ultimo content che è quello che è stato modificato, allora elimina la song
                DELETE FROM song 
                WHERE id = idSong;
        END IF;
        
    END IF;

    -- aggiorno in ogni caso il contenuto
    UPDATE content c
    SET c.content = contentIN
    WHERE c.id = idContentIN;
        
END$$
DELIMITER ;



